parameters.command:
sudo bash -c "echo ' server {
    listen 80;
    server_name dvs-dev.anilrajrimal.com.np;    # domain name to respond to
    access_log /var/log/nginx/dvs-dev.anilrajrimal.com.np.access;
    error_log /var/log/nginx/dvs-dev.anilrajrimal.com.np.error;

    client_max_body_size 500M;            # Allow uploads/transper to 100MB.
    client_body_buffer_size 128k;

    gzip on;            # Allows compression; can be CPU intensive
    gzip_min_length 150;
    gzip_types    text/plain text/css application/javascript application/x-javascript text/javascript image/x-icon image/svg+xml image/png application/json ;

              root /home/ubuntu/Projects/dvs-v3-frontend/dist;
              index  index.html index.htm;
     location / {
              add_header Access-Control-Allow-Origin *;
              try_files \$uri /index.html;            # if no endpoint found try index.html
             }

}' > /etc/nginx/sites-enabled/dvs-dev.anilrajrimal.com.np.conf"

